        private class State
        {
            public State(byte[] box)
            {
                _box = box;
                _i = 0;
                _j = 0;
            }

            public byte[] Crypt(IReadOnlyList<byte> data, int len)
            {
                byte[] new_bytes = new byte[len];

                int enc_index = 0;
                while (true)
                {
                    _i++;
                    _i &= 0xff;

                    if (_i >= 0x100)
                        break;

                    byte char_1 = _box[_i];

                    _j += _box[_i];
                    _j &= 0xff;

                    if (_j >= 0x100)
                        break;

                    byte char_2 = _box[_j];

                    _box[_j] = char_1;
                    _box[_i] = char_2;

                    byte index_2 = (byte)(_box[_i] + _box[_j]);

                    if (enc_index < len)
                    {
                        new_bytes[enc_index] = (byte)(data[enc_index] ^ _box[index_2]);
                        enc_index++;
                        if (enc_index >= len)
                            break;
                    }
                }
                return new_bytes;
            }

            private readonly byte[] _box;

            private int _i;
            private int _j;
        }


        private static State recvState;
        private static State sendState;

        public static byte[] Encrypt(byte[] input)
        {
            var output_bytes = sendState.Crypt(input, input.Length);
            return output_bytes;
        }

        public static byte[] Decrypt(byte[] input)
        {
            var output_bytes = recvState.Crypt(input, input.Length);
            return output_bytes;
        }

        public static void Reset()
        {
            byte[] box1 = new byte[0x100];
            byte[] box2 = new byte[0x100];

            Array.Clear(box1, 0, 0x100);
            Array.Clear(box2, 0, 0x100);

            Array.Copy(Master_Key, 0, box1, 0, 0x100);
            Array.Copy(Recv_Master_Key, 0, box2, 0, 0x100);

            sendState = new State(box1);
            recvState = new State(box2);
        }

        public static string FixDeviceId(Guid deviceId)
        {
            var id = deviceId.ToString();
            // Last byte of the ID is the last byte of the total GUID ASCII Value's Sum
            id = id.Substring(0, id.Length - 2);
            int sum = 0;
            for (int i = 0; i < id.Length; i++)
            {
                sum += id[i];
            }
            id += (sum & 0xff).ToString("X2"); // need the last byte
            return id;
        }
